// Get today's day number (1-31)
const today = new Date();
const todayDay = today.getDate();
console.log("Today's day:", todayDay);

// Filter clients with expiration day matching today
const clientsWithExpirationToday = $input.all().filter(item => {
  const clientData = item.json;
  const expirationDay = clientData.Vencimiento;

  // Check if the expiration day matches today's day
  // Handle both numeric and string inputs
  const dayNumber = typeof expirationDay === 'string' ? parseInt(expirationDay) : expirationDay;

  return dayNumber === todayDay;
});

// Return clients with expiration day today or empty array if none found
if (clientsWithExpirationToday.length > 0) {
  console.log(`Found ${clientsWithExpirationToday.length} clients with expiration day ${todayDay}`);
  return clientsWithExpirationToday;
} else {
  console.log(`No clients with expiration day ${todayDay}`);
  return [];
}

// Get clients data with expiration today
const clientsData = $input.all();

// If no clients with expiration today, return empty
if (!clientsData || clientsData.length === 0) {
  return [];
}

// Create output for each client with personalized message
const output = [];

for (const client of clientsData) {
  const clientData = client.json;

  // Create personalized message
  const personalizedMessage = `Muy buenos d√≠as ${clientData.Nombre}. Se le recuerda que hoy vence su IVA por si tiene algunos comprobantes que entregar esperamos atentamente`;

  output.push({
    phone_number: clientData.Numero,
    client_name: clientData.Nombre,
    last_name: clientData.Apellido,
    expiration_date: clientData.Vencimiento,
    message: personalizedMessage
  });
}

return output;
